positives.query:
SELECT ?item ?name ?article WHERE {
    ?item p:P31/ps:P31/wdt:P279* wd:Q1363241 .
    ?article schema:about ?item ;
            schema:inLanguage "en" ;
            schema:isPartOf <https://en.wikipedia.org/> ;
            schema:name ?name .
}
LIMIT 50

negatives.query:
SELECT ?item ?name ?article WHERE {
    ?item p:P31/ps:P31/wdt:P279* wd:Q4886 .
    ?article schema:about ?item ;
            schema:inLanguage "en" ;
            schema:isPartOf <https://en.wikipedia.org/> ;
            schema:name ?name .
    FILTER NOT EXISTS { ?item p:P31/ps:P31/wdt:P279* wd:Q1363241 }
}
LIMIT 50
